import { Component, OnInit } from '@angular/core'
import { FormGroup } from '@angular/forms';
import { FormlyFieldConfig } from '@ngx-formly/core';
import { UserModel } from 'src/app/models/user-model';
import {FormBuilder, Validators} from '@angular/forms';
import {BreakpointObserver} from '@angular/cdk/layout';
import {StepperOrientation} from '@angular/material/stepper';
import {Observable} from 'rxjs';
import {map} from 'rxjs/operators';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  stepperOrientation: Observable<StepperOrientation>;

  constructor(private _formBuilder: FormBuilder, breakpointObserver: BreakpointObserver) {
    this.stepperOrientation = breakpointObserver.observe('(min-width: 800px)')
      .pipe(map(({matches}) => matches ? 'horizontal' : 'vertical'));
  }

  ngOnInit(): void {
  }

  form = new FormGroup({});
  passwordForm = new FormGroup({});
  model ={} as UserModel;

  fields: FormlyFieldConfig[] = [
    {
      key: 'name',
      type: 'input',
      templateOptions: {
        label: 'Nume utilizator',
        placeholder: 'Introduceti numele utilizator: ',
        required: true,
      }
    },
    {
      key: 'email',
      type: 'input',
      templateOptions: {
        label: 'Adresa de email',
        placeholder: 'Introduceti adresa de email: ',
        required: true,
      }
    },
  ];

  passwordFields: FormlyFieldConfig[]=[
      {
        key: 'password',
        type: 'input',
        templateOptions: {
          label: 'Va rugam alegeti o parola',
          placeholder: 'Introduceti parola: ',
          required: true,
        }
      }
  ];


  onSubmit() {
    this.model.role=2;
    if ((this.model.name!=null)&&(this.model.password!=null)&&(this.model.email!=null))
      console.log(this.model);
    else
      console.log("Eroare");
  }

}
<div class="register-container" >

    <mat-card>
        <mat-icon>restaurant</mat-icon>
        <mat-card-title>Inregistrare</mat-card-title>
        <mat-card-subtitle>Va rugam sa completati formularul de inregistrare</mat-card-subtitle>
        <mat-card-content>


              <!-- <ng-container [ngSwitch]="stepperOrientation | async">
                <div *ngSwitchCase="'horizontal'">Make your screen smaller to see a vertical stepper</div>
                <div *ngSwitchCase="'vertical'">Make your screen larger to see a horizontal stepper</div>
              </ng-container>
               -->
              <mat-stepper
                class="example-stepper"
                [orientation]="(stepperOrientation | async)!">

                <mat-step [stepControl]="form" label="Introduceti numele si email-ul">
                  <form [formGroup]="form">
                        <formly-form [form]="form" [fields]="fields" [model]="model"></formly-form>

                    <div>
                      <button mat-button matStepperNext>Next</button>
                    </div>
                  </form>
                </mat-step>
                <mat-step [stepControl]="passwordForm" label="Introduceti parola">
                  <form [formGroup]="passwordForm">
                    <formly-form [form]="passwordForm" [fields]="passwordFields" [model]="password"></formly-form>
                    <div>
                      <button mat-button matStepperPrevious>Back</button>
                      <button mat-button matStepperNext>Next</button>
                    </div>
                  </form>
                </mat-step>
                <!--<mat-step [stepControl]="thirdFormGroup" label="Fill out your phone number">
                  <form [formGroup]="thirdFormGroup">
                    <mat-form-field appearance="fill">
                      <mat-label>Phone number</mat-label>
                      <input matInput formControlName="thirdCtrl" placeholder="Ex. 12345678" required>
                    </mat-form-field>
                    <div>
                      <button mat-button matStepperPrevious>Back</button>
                      <button mat-button matStepperNext>Next</button>
                    </div>
                  </form>
                </mat-step> -->
                <mat-step>
                  <ng-template matStepLabel>Ultimul pas</ng-template>
                  <button class="register-button" mat-button type="submit" (click)="onSubmit()">Inregistrare</button>
                  <div>
                    <button mat-button matStepperPrevious>Back</button>
                  </div>
                </mat-step>
              </mat-stepper>


        </mat-card-content>
    </mat-card>
</div>
