import { Component, OnInit } from '@angular/core'
import { FormlyFieldConfig,FormlyFormOptions } from '@ngx-formly/core';
import { UserModel, UserPasword } from 'src/app/models/user-model';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { BreakpointObserver } from '@angular/cdk/layout';
import { StepperOrientation } from '@angular/material/stepper';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';


@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {

  stepperOrientation: Observable<StepperOrientation>;

  constructor(private _formBuilder: FormBuilder, breakpointObserver: BreakpointObserver) {
    this.stepperOrientation = breakpointObserver.observe('(min-width: 800px)')
      .pipe(map(({matches}) => matches ? 'horizontal' : 'vertical'));
  }

  ngOnInit(): void {

  }

  form = new FormGroup({});
  passwordForm = new FormGroup({});
  model ={} as UserModel;
  pwdObject ={} as UserPasword;

  fields: FormlyFieldConfig[] = [
    {
      key: 'name',
      type: 'input',
      templateOptions: {
        label: 'Nume utilizator',
        placeholder: 'Introduceti numele utilizator: ',
        required: true,
      }
    },
    {
      key: 'email',
      type: 'input',
      templateOptions: {
        label: 'Adresa de email',
        placeholder: 'Introduceti adresa de email: ',
        pattern: "^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$",
        required: true,
      },
      validation: {
        messages: {
          pattern: (error, field: FormlyFieldConfig) => `Adresa de email invalida`,
        },
    },
  }];

  passwordFields: FormlyFieldConfig[]=[
      {
        key: 'password',
        type: 'input',
        templateOptions: {
          label: 'Va rugam alegeti o parola',
          placeholder: 'Introduceti parola: ',
          type: 'password',
          required: true,
          minLength: 6,
        }
      },
      {
        key: 'passwordChck',
        type: 'input',
        templateOptions: {
          label: 'Reintroduceti parola',
          placeholder: 'Introduceti parola: ',
          type: 'password',
          required: true,
          minLength: 6,
        }, 
    },
  ];

  onPasswordCheck():boolean{
    if (this.pwdObject.password==this.pwdObject.passwordChck){
      return false;
    }

    return true;
  }

  onSubmit() {
    this.model.role=2;
    if ((this.model.name!=null)&&(this.model.password!=null)&&(this.model.email!=null))
      console.log(this.model);
    else
      console.log("Eroare");
  }
}
